      <img src="{{~>asset_url url='images/404.png'~}}" class="responsive">
    </div>
  </div>
</div>
{{#ifAll @root.data.supplimentaryResponses.not_found (compare @root.data.supplimentaryResponses.not_found.hits.total ">" 1) ~}}
<hr />
<h3>Other relevant content</h3>
{{#each @root.data.supplimentaryResponses.not_found.hits.hits ~}}
{{#compare @index "===" 0 ~}}
{{else}}
{{>highlight_result}}
{{/compare ~}}
{{/each ~}}
{{else}}
<h3>Try searching for a topic</h3>
{{/ifAll ~}}

<script>
gtag('event', '404', {
  event_category: '{{name}}',
  event_label: '{{request.params.uri.pathname}}'
});
</script>
{{/if ~}}

